import { useState } from "react"
const api_url = 'http://192.168.1.58:3000'

function App() {

const [books, setbooks] = useState([])

const search = async (category, term) => {
  let res = await fetch(`${api_url}/search/${category}/${term}`)
  let data = await res.json()

  console.log(data.results)
  setbooks(data.results)
}


//book cards for search results
const BookCard = ({ bobj }) => {
  return (
    <div>
      <h1> {bobj.title} </h1>
      <h3> - {bobj.authors} </h3>
      <p>-----------------------------------------------------------------------------</p>
    </div>
  )
}

//search component
const SearchForm = () => {

  //these 2 are used in SearchForm Component
  const [cat, setcat] = useState('title')
  const [term, setterm] = useState('search here')

  const eatSubmit = (e) => {
    search(cat, term)
    e.preventDefault()
  }

  return (
    <div>
      <p> Search </p>
      <form onSubmit={eatSubmit} >
        <label>
          <select value={cat} onChange={(e) => setcat(e.target.value)}>
            <option value="authors">authors</option>
            <option value="title">title</option>
          </select>
          <p></p>
          <input type="text" 
            name="term" 
            value={term} 
            onChange={(e) => {setterm(e.target.value)}} />
          <p></p>
        </label>
        <button type="submit" >Search</button>
      </form>
    </div>
  )
}

const PostForm = () => {

  const  [Bookname, setBookname] = useState('Book name goes here')
  const  [Author, setAuthor] = useState('Author\'s here')

  const PostBook = (e) =>{
    const book = {
      book: {
        title: Bookname,
        authors: Author
      }
    }

    var jsonn = JSON.stringify(book)
    console.log(book)

    fetch(`${api_url}/add`, {
      method: 'POST',
      mode: 'cors',
      body: jsonn
    })
  }

  return (
    <>
      <form onSubmit={PostBook}>
        <label>Add a Book</label>
        <p></p>
        <input type="text"
          value={Author}
          onChange={(e) => {setAuthor(e.target.value)}} ></input>
        <p></p>
        <input type="text"
          value={Bookname}
          onChange={(e) => {setBookname(e.target.value)}} ></input>
        <p></p>
        <button type="submit">Add this Book</button>
      </form>
    </>
  )
}

return (
  <div className="App">
    <SearchForm/>
    <PostForm/>
    {
      books?.length > 0 ? (
        <div className="container">
          {
            books.map((book) => (
              <BookCard key={book.id} bobj={book} />
            ))
          }
        </div>
      ) : (
        <div>
          <h2>
            Welcome to the Book store
          </h2>
          <p>powered by kaggle</p>
        </div>
      )
    }
  </div>
)
}

export default App;
