import { useState } from "react"
import './App.css'
const api_url = 'http://192.168.1.58:3000'

function App() {

const [books, setbooks] = useState([])

const search = async (category, term) => {
  let res = await fetch(`${api_url}/search/${category}/${term}`)
  let data = await res.json()

  console.log(data.results)
  setbooks(data.results)
}


//book cards for search results
const BookCard = ({ bobj }) => {
  return (
    <div>
      <h6 className="underline" > {bobj.id} </h6>
      <h1> {bobj.title} </h1>
      <h3> - {bobj.authors} </h3>
      <p>-----------------------------------------------------------------------------</p>
    </div>
  )
}

//search component
const SearchForm = () => {

  //these 2 are used in SearchForm Component
  const [cat, setcat] = useState('title')
  const [term, setterm] = useState('search here')

  const eatSubmit = (e) => {
    search(cat, term)
    e.preventDefault()
  }

  return (
    <div>
      <p> Search </p>
      <form onSubmit={eatSubmit} >
        <label>
          <select value={cat} onChange={(e) => setcat(e.target.value)}>
            <option value="authors">authors</option>
            <option value="title">title</option>
          </select>
          <p></p>
          <input type="text" 
            name="term" 
            value={term} 
            onChange={(e) => {setterm(e.target.value)}} />
          <p></p>
        </label>
        <button type="submit" >Search</button>
      </form>
    </div>
  )
}

//post componenet
const PostForm = () => {

  const  [Bookname, setBookname] = useState("Book name goes here")
  const  [Author, setAuthor] = useState("Author's here")

  const PostBook = (e) =>{
    var book = {
      "title": `${Bookname}`,
      "authors": `${Author}`
    }

    var form = []
    for (var key in book) {
      let enkey = encodeURIComponent(key)
      let enval = encodeURIComponent(book[key])
      form.push(enkey + "=" + enval)
    }
    form = form.join("&")

    fetch(`${api_url}/add`, {
      method: 'POST',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
      },
      body: form
    })
    e.preventDefault()
  }

  return (
    <>
      <form onSubmit={PostBook}>
        <label>Add a Book</label>
        <p></p>
        <input type="text"
          value={Author}
          onChange={(e) => {setAuthor(e.target.value)}} ></input>
        <p></p>
        <input type="text"
          value={Bookname}
          onChange={(e) => {setBookname(e.target.value)}} ></input>
        <p></p>
        <button type="submit">Add this Book</button>
      </form>
    </>
  )
}

//update component
const UpdateForm = () => {

  const [Id, setId] = useState(-1)
  const [Cat, setCat] = useState('title')
  const [Data, setData] = useState('')

  const handleput = (e) => {

    var book = {
      "id": Id,
      "category": `${Cat}`,
      "data": `${Data}`
    }

    console.log(book)

    var form = []
    for (var key in book) {
      let enkey = encodeURIComponent(key)
      let enval = encodeURIComponent(book[key])
      form.push(enkey + "=" + enval)
    }
    form = form.join("&")

    fetch(`${api_url}/update`, {
      method: 'PUT',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
      },
      body: form
    })
    e.preventDefault()

  }

  return (
    <>
      <form onSubmit={handleput} >
        <label>update an entry</label>
        <select value={Cat} onChange = {(e) => {setCat(e.target.value)}} >
          <option value="authors">author</option>
          <option value="title">title</option>
        </select>
        <p>ID:</p>
        <input value={Id} onChange = {(e) => {setId(e.target.value)}} />
        <p>Data to update</p>
        <input value={Data} onChange = {(e) => {setData(e.target.value)}} />
        <button type="submit"> Submit </button>
      </form>
    </>
  )
}

const RemoveForm = () => {

  const [id, setid] = useState(-1)

  const handle = (e) => {

    fetch(`${api_url}/remove/${id}`, {
      method: "DELETE"
    })
    e.preventDefault()
  }

  return (
    <>
      <form onSubmit={handle} >
        <label>remove a book</label>
        <p></p>
        <input value={id} onChange={(e)=>{setid(e.target.value)}} />
        <button type="submit">Delete</button>
      </form>
    </>
  )
}

return (
  <div className="App">
    <SearchForm/>
    <PostForm/>
    <UpdateForm/>
    <RemoveForm/>
    {
      books?.length > 0 ? (
        <div className="font-bold">
          {
            books.map((book) => (
              <BookCard key={book.id} bobj={book} />
            ))
          }
        </div>
      ) : (
        <div>
          <h2>
            Welcome to the Book store
          </h2>
          <p>powered by kaggle</p>
        </div>
      )
    }
  </div>
)
}

export default App;
