import { useEffect, useState } from "react"
const api_url = 'http://192.168.1.58:3000'

function App() {

const [books, setbooks] = useState([])

//these 2 are used in SearchForm Component
const [cat, setcat] = useState('authors')
const [term, setterm] = useState('man')

const search = async (category, term) => {
  let res = await fetch(`${api_url}/search/${category}/${term}`)
  let data = await res.json()

  console.log(data.results)
  setbooks(data.results)
  console.log(books)
}

useEffect(() => {
  search(cat, term)
}, [term])

//book cards for search results
const BookCard = ({ bobj }) => {
  return (
    <div>
      <p>
        <h1> {bobj.title} </h1>
        <h3> - {bobj.authors} </h3>
      </p>
      <p>-----------------------------------------------------------------------------</p>
    </div>
  )
}

const SearchForm = () => {


  return (
    <div>
      <p> Search </p>
      <form>
        <label>
          <select value={cat} onChange={(e) => setcat(e.target.value)}>
            <option value="authors">authors</option>
            <option value="title">title</option>
          </select>
          <p></p>
          <input type="text" 
            name="term" 
            value={term} 
            onChange={(e) => {setterm(e.target.value)}} />
          <p></p>
        </label>
      </form>
      <button onClick={() => {search(cat,term)}} >Search</button>
    </div>
  )
}

return (
  <div className="App">
    <SearchForm/>
    {
      books?.length > 0 ? (
        <div className="container">
          {
            books.map((book) => (
              <BookCard bobj={book} />
            ))
          }
        </div>
      ) : (
        <div>
          <h2>
            if you feel low, STOP MAKING A "C" WITH YOUR SPINE
          </h2>
        </div>
      )
    }
  </div>
)
}

export default App;
