import { useEffect, useState } from "react"
const api_url = 'http://192.168.1.58:3000'

function App() {

const {books, setbooks} = useState([])

const search = async (title) => {
  let res = await fetch(`${api_url}/search?searchterm=${title}`)
  let data = await res.json()
  setbooks(data.results)
}

useEffect(() => {
  let tmp = (search('Alice'))
}, [])

const BookCard = ({ bobj }) => {
  return (
    <div>
      <p>
        <h2> {bobj.title} </h2>
      </p>
      <p>
        <h6> {bobj.authors} </h6>
      </p>
    </div>
  )
}


  return (
    <div className="App">
      {
        books?.length > 0 ? (
          <div className="container">
            {
              books.map((book) => {
                <BookCard bobj={book} />
              })
            }
          </div>
        ) : (
          <div>
            <h2>
              if you feel low, STOP MAKING A "C" WITH YOUR SPINE
            </h2>
          </div>
        )
      }
    </div>
  )
}

export default App;
